<template>
	<div class="country-list">
		<div class="detial">this is detail page</div>
		<div class="country-border" ref="countryList">
      <div v-for="(item, index) in bankModify" :key="index">
				<div :data-en="item.firstName" v-if="item.first">{{item.firstName}}</div>
				<div class="item">
					<div>{{item.title}}</div>
				</div>
			</div>
			<div class="char-list-border">
				<ul
					class="char-list"
					ref="charBar"
					@touchstart="e => touchStart(e)"
					@touchmove="e => touchMove(e)"
					@touchend="e => touchEnd(e)"
				>
					<li class="char-item" v-for="(char,index) in charList" :key="index">{{char}}</li>
				</ul>
			</div>
			<div class="char-tip" v-show="isTouching">{{lastChar}}</div>
		</div>
	</div>
</template>

<script>
import { country, goods } from "./data.js";

export default {
	data() {
		return {
			country,
			charList: "*ABCDEFGHIJKLMNOPQRSTUVWXYZ#",
			isTouching: false,
			boxClientTop: 50,
			lastChar: "A",
		};
	},
	computed: {
		bankModify(arr) {
			// var goods = [
      //     { id: 196, title: "广东农信" },
      //     { id: 199, title: "浙江农信银行" },
      //     { id: 40, title: "顺德农商银行" },
      //     { id: 41, title: "尧都区农村信用联社" },
      //     { id: 48, title: "成都银行" },
      //     { id: 65, title: "上海浦东" },
      //     { id: 72, title: "财付通" },
      //     { id: 191, title: "贵州银行" },
      // ];
      let bankpro = this.doSort(goods,"title",true);
      console.log(bankpro,'bankpro')
      let name = '';
      let typeArr = bankpro.map(v=>{
          let firstName = this.getFirstLetter(v.title).charAt(0).toUpperCase();
          v.firstName = firstName
          // 第一个不一样的字母 first 为true
          if(firstName!=name){
              name = firstName
              v.first = true;
          }else{
              v.first = false;
          }
          return v
      })
      return typeArr
			// 'Angola 安哥拉',排序
		},

		countryModify() {
			const obj = {};
			const arr = this.country.map((item, index) => {
				const i = item.lastIndexOf(" ");
				const en = item.slice(0, i);
				// const ch = item.slice(i + 1);
				let first = false;
				if (obj[en.slice(0, 1)]) {
					first = false;
				} else {
					first = true;
					obj[en.slice(0, 1)] = true;
				}
				console.log(item, "item");
				return {
					en: en,
					// ch: ch,
					first: first,
				};
			});
			console.log(arr, "arrfff");
			return arr;
		},
	},
	methods: {
		
		doSort(arr, property, sequence) {
			let _this = this;
			// arr：排序的数组，数组对应对象属性值：property，sort：true是降序
			function compareArray(arr, property, sequence) {
				for (var j = 0; j < arr.length - 1; j += 1) {
					// 两两比较，如果前一个比后一个大，则交换位置。
					for (var i = 0; i < arr.length - 1 - j; i += 1) {
						// 取出数组对应属性进行排序
						var before = compareString(
							arr[i][property],
							arr[i + 1][property]
						);
						if (before === 1) {
							var temp = arr[i];
							arr[i] = arr[i + 1];
							arr[i + 1] = temp;
						}
					}
        }
        
				if (sequence) {
					arr.reverse();
				}
				return arr;
			}
			// code从-1至1依次是str1排在str2后，相等，前
			function compareString(str1, str2) {
				var str1Length = str1.length;
				var str2Length = str2.length;
				var StringLength = Math.min(str1Length, str2Length);
				var StringCode = "";
				var code = 0;
				// 字符串长度短的，排序靠前
				if (StringLength > 0) {
					for (var i = 0; i < StringLength; i += 1) {
						StringCode = compareCharacter(str1[i], str2[i]);
						if (StringCode === -1) {
							code = -1;
							break;
						} else if (StringCode === 1) {
							code = 1;
							break;
						} else {
							code = 0;
						}
					}
					if (code === 0 && str1Length !== str2Length) {
						code = str1Length - str2Length <= -1 ? 1 : -1;
					}
				} else {
					// 对于空字符 串单独处理
					code = 0;
					if (code === 0 && str1Length !== str2Length) {
						code = str1Length - str2Length <= -1 ? 1 : -1;
					}
				}
				return code;
			}
			// 单字符排序，优先级：（特殊字符>数字>英文字母>汉字）
			function compareCharacter(Letter1, Letter2) {
				var Letter1TypeNum = DetermineType(Letter1);
				var Letter2TypeNum = DetermineType(Letter2);
				var before = "";
				var after = "";
				var code = "";
				// 不同类型排序
				if (Letter1TypeNum !== Letter2TypeNum) {
					code = Letter1TypeNum < Letter2TypeNum;
					return code ? 1 : -1;
				} else {
					// 同类型对比
					if (Letter1TypeNum === 0) {
						return (code = 0);
					}

					if (Letter1TypeNum === 1) {
						before = Letter1;
						after = Letter2;
					}
					// 不区分大小写，依据英文字母Unicode码大小排序，a-z的Unicode码依次增加
					// if (Letter1TypeNum === 2) {
					// 	before = Letter1.toLocaleLowerCase().charCodeAt(0);
					// 	after = Letter2.toLocaleLowerCase().charCodeAt(0);
					// }
					if (Letter1TypeNum === 3) {
						// 转换成汉字对应拼音的首字母进行比较
						before = _this
							.getFirstLetter(Letter1)
							.toLocaleLowerCase()
							.charCodeAt(0);
						after = _this
							.getFirstLetter(Letter2)
							.toLocaleLowerCase()
							.charCodeAt(0);
					}
				}
				if (before > after) {
					code = -1;
				} else if (before === after) {
					code = 0;
				} else {
					code = 1;
				}

				return code;
			}
			// 判断单字符类型，返回值0到3依次是特殊字符，数字，字母，汉字类型
			function DetermineType(Letter) {
				// 判断类型
				var isNum = !isNaN(Letter);
				var isLetter = /[_a-zA-Z]/.test(Letter);
				var isChinese = !/[^\u4E00-\u9FA5]/.test(Letter);
				var typeNum = "";
				if (isNum) {
					typeNum = 1;
        } 
        // else if (isLetter) {
				// 	typeNum = 2;
        // } 
        else if (isChinese||isLetter) {
					typeNum = 3;
				} else {
					typeNum = 0;
				}
				return typeNum;
			}
			return compareArray(arr, property, sequence); // 根据'goodName'降序排序
			// console.log(goods, "goods");
		},
		// 返回汉字拼音首字母（大写）
		getFirstLetter(str) {
			var pinyin_dict_firstletter = {};
			// 拼音首字母字典文件
			pinyin_dict_firstletter.all =
				"";
			var dict = {};
			if (!str || /^ +$/g.test(str)) return "";
			dict.firstletter = pinyin_dict_firstletter;
			if (dict.firstletter) {
				// 使用首字母字典文件
				var result = [];
				for (var i = 0; i < str.length; i++) {
					var unicode = str.charCodeAt(i);
					var ch = str.charAt(i);
					if (unicode >= 19968 && unicode <= 40869) {
						ch = dict.firstletter.all.charAt(unicode - 19968);
					}
					result.push(ch);
				}
				return result.join("");
			}
		},
		toList() {
			this.$router.go(-1);
		},
		toPost() {
			this.$router.push("post");
		},
		touchStart(e) {
			e.preventDefault();
			this.isTouching = true;
			const char = this.getChar(e.touches[0].clientY);
			this.gotoChar(char);
		},
		touchMove(e) {
			e.preventDefault();
			const char = this.getChar(e.touches[0].clientY);
			this.gotoChar(char);
		},
		touchEnd(e) {
			e.preventDefault();
			this.isTouching = false;
		},
		getChar(clientY) {
			const charHeight =
				this.$refs.charBar.offsetHeight / this.charList.length;
			const index = Math.floor(
				(clientY - this.boxClientTop) / charHeight
			);
			return this.charList[index];
		},
		gotoChar(char) {
			if (char === this.lastChar) {
				return false;
			}
			this.lastChar = char;
			if (char === "*") {
				this.$refs.countryList.scrollTop = 0;
			} else if (char === "#") {
				console.log(
					this.$refs.countryList.scrollTop,
					this.$refs.countryList.scrollHeight
				);
				this.$refs.countryList.scrollTop = this.$refs.countryList.scrollHeight;
			}
			const target = document.querySelector('[data-en="' + char + '"]');
			if (target) {
				target.scrollIntoView();
			}
		},
	},
};
</script>

